<template>
  <div class="icons">
    <swiper>
      <swiper-slide v-for="(page, index) of pages" :key="index">
        <div class="icon"
             v-for="item of page"
             :key="item.id">
          <div class="icon-img">
           <img class='icon-img-content' :src='item.imgUrl'>
       </div>
        <p class="icon-desc">{{item.desc}}</p>
  </div>
      </swiper-slide>
     </swiper>

  </div>
</template>

<script>

export default {
  name: 'HomeIcons',
  data () {
    return {
      iconsList: [{
        id: '001',
        imgUrl: 'data:image/jpeg;base64,',
        desc: 'Attraction'
      }, {
        id: '002',
        imgUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcQ-apka9nFGtqrItd9fyBx5aoHkhoLOwKAppQ&usqp=CAU',
        desc: 'Ski'
      }, {
        id: '003',
        imgUrl: 'data:image/png;base64,',
        desc: 'Hot spring'
      }, {
        id: '004',
        imgUrl: 'data:image/png;base64,',
        desc: 'Botanical garden'
      }, {
        id: '005',
        imgUrl: 'data:image/png;base64,',
        desc: 'Motel'
      }, {
        id: '006',
        imgUrl: 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTFdAPpkHqUt4W4hFoJ4o-P5SDoxEJ6xEaNEw&usqp=CAU',
        desc: 'Restaurant'
      }, {
        id: '007',
        imgUrl: 'data:image/png;base64,',
        desc: 'Viewpoint'
      }, {
        id: '008',
        imgUrl: 'https://images.vexels.com/media/users/3/128176/isolated/lists/4086178bba7e381b9324267248ac78f8-location-pointer-travel-icon.png',
        desc: 'Location'
      }, {
        id: '009',
        imgUrl: 'https://icons-for-free.com/iconfiles/png/512/citycons+public+transport+travel+icon-1320136425089024798.png',
        desc: 'Route'
      }]
    }
  },
  computed: {
    pages () {
      const pages = []
      this.iconsList.forEach((item, index) => {
        const page = Math.floor(index / 8)
        if (!pages[page]) {
          pages[page] = []
        }
        pages[page].push(item)
      })
      return pages
    }
  }
}
</script>

